mapper.select:
select 

from est_data_cache
where data_cache_id = #{dataCacheId,jdbcType=CHAR}
  and data_cache_name = #{dataCacheName,jdbcType=CHAR}
  and data_cache_desc = #{dataCacheDesc,jdbcType=CHAR}

mapper.delete:
delete from est_data_cache
where data_cache_id = #{dataCacheId,jdbcType=CHAR}
  and data_cache_name = #{dataCacheName,jdbcType=CHAR}
  and data_cache_desc = #{dataCacheDesc,jdbcType=CHAR}
