mapper.select:
select 

from est_sub_bus
where project_id = #{projectId,jdbcType=CHAR}
  and bus_id = #{busId,jdbcType=CHAR}
  and sub_bus_id = #{subBusId,jdbcType=CHAR}

mapper.delete:
delete from est_sub_bus
where project_id = #{projectId,jdbcType=CHAR}
  and bus_id = #{busId,jdbcType=CHAR}
  and sub_bus_id = #{subBusId,jdbcType=CHAR}
