  and project_id = #{projectId,jdbcType=CHAR}
  and env_id = #{envId,jdbcType=CHAR}
  and dest_addr = #{destAddr,jdbcType=CHAR}
  and cloud_project_id = #{cloudProjectId,jdbcType=CHAR}

mapper.insert:
