mapper.select:
select 

from est_node
where dta_id = #{dtaId,jdbcType=CHAR}
  and node_name = #{nodeName,jdbcType=CHAR}

mapper.delete:
delete from est_node
where dta_id = #{dtaId,jdbcType=CHAR}
  and node_name = #{nodeName,jdbcType=CHAR}

mapper.update:
update est_node


    node_id = #{nodeId,jdbcType=CHAR},


    node_desc = #{nodeDesc,jdbcType=VARCHAR},


where dta_id = #{dtaId,jdbcType=CHAR}
  and node_name = #{nodeName,jdbcType=CHAR}

update est_node
set node_id = #{nodeId,jdbcType=CHAR},
  node_desc = #{nodeDesc,jdbcType=VARCHAR}
where dta_id = #{dtaId,jdbcType=CHAR}
  and node_name = #{nodeName,jdbcType=CHAR}
