mapper.select:
select 

from est_sysinfo_mng
where project_id = #{projectId,jdbcType=CHAR}
  and env_id = #{envId,jdbcType=CHAR}
  and cloud_project_id = #{cloudProjectId,jdbcType=CHAR}

mapper.delete:
delete from est_sysinfo_mng
where project_id = #{projectId,jdbcType=CHAR}
  and env_id = #{envId,jdbcType=CHAR}
  and cloud_project_id = #{cloudProjectId,jdbcType=CHAR}
